class Keypad
  attr_reader :code

  def initialize
    @position = 5
    @code = []
  end

  def move(letter)
    case letter
    when 'U'
      @position -= 3 unless @position < 4
    when 'L'
      @position -= 1 unless [1,4,7].include?(@position)
    when 'R'
      @position += 1 unless [3,6,9].include?(@position)
    when 'D'
      @position += 3 unless @position > 6
    end
  end

  def read_line(line)
    line.chars.each { |c| move(c) }
    @code << @position
  end
end

seq1 = "ULL
RRDDD
LURDL
UUUUD"
key1 = Keypad.new
seq1.each_line { |line| key1.read_line(line) }
puts key1.code.join
